import React, { Component } from 'react'
import Title from './Title'
import { FaCocktail, FaHiking, FaShuttleVan, FaBeer } from 'react-icons/fa'
import { MaterialDesignicons } from 'react-icons'
import { AiFillSafetyCertificate } from "react-icons/ai";
import { MdOutlineAssignmentTurnedIn } from "react-icons/md"

export default class Services extends Component {
    state = {
        services: [
            {
                icon: <FaCocktail />,
                title: "Lifetime Access to the course",
                info: "Lorem ipsum dolor sit amet, consectetur adipisicing elit. Pariatur"
            },
            {
                icon: <FaHiking />,
                title: "Track bsed Learning",
                info: "Lorem ipsum dolor sit amet, consectetur adipisicing elit. Pariatur"
            },
            {
                icon: <AiFillSafetyCertificate />,
                title: "Course Completion Certificate",
                info: "Lorem ipsum dolor sit amet, consectetur adipisicing elit. Pariatur"
            },
            {
                icon: <MdOutlineAssignmentTurnedIn />,
                title: "Unlimited Beer",
                info: "Lorem ipsum dolor sit amet, consectetur adipisicing elit. Pariatur"
            },

        ]
    }
    render() {
        return (
            <div className="container-fluid services">
                <Title title="Benefits" />
                <div className="row">
                    {this.state.services.map((item, index) => {
                        return (
                            <div className="col-md-4 col-lg-3 col-12 mx-auto my-3" key={index}>
                                <div className="card shadow-lg border-0 p-4">
                                    <article className="service">
                                        <span>{item.icon}</span>
                                        <h6>{item.title}</h6>
                                        <p>{item.info}</p>
                                    </article>
                                </div>
                            </div>
                        )
                    })}
                </div>
            </div>
        )
    }
}
